function Link(props) {
  return (
    <a href="/stories" className="navbar-link">
      {props.children}
      <style jsx>
        {`
          
          .nav-link {
            color: grey;
            font-family: Roboto;
            font-size: 16px;
            font-weight: 700;
            line-height: 19px;
            text-align: center;
          }
          .is-active {
            font-size: 1.8rem;
            color: black;
          }
          .is-active::after {
            content: "";
            height: 1rem;
            width: 1rem;
            background-color: var(--color-secondary);
            position: absolute;
            bottom: -1.5rem;
            left: 50%;
            transform: translateX(-50%);
          }
        `}
      </style>
       
    </a>
   
  );

}

export default Link;
