function Navbar() {
  return (
    <nav className="navbar">
      <div className="container">
        {/* <a href="/stories" className="navbar-link">
          Stories
        </a>
        <a href="/" className="navbar-link is-active">
          Covid Anchor
        </a>
        <a href="/cases" className="navbar-link">
          Cases
        </a> */}

        <Link>Stories</Link>
        <Link>Covid Anchor</Link>
        <Link>Cases</Link>
      </div>
      
      <style jsx>
        {`
          .navbar {
            height: 100%;
            width: 12rem;
            background-color: white;
            display: flex;
            justify-content: center;
            align-items: center;
          }
          .container {
            min-width: 30rem;
            display: flex;
            justify-content: space-between;
            transform: rotate(-90deg);
          }
          .nav-link {
            color: grey;
            font-family: Roboto;
            font-size: 16px;
            font-weight: 700;
            line-height: 19px;
            text-align: center;
          }
          .is-active {
            font-size: 1.8rem;
            color: black;
          }
          .is-active::before {
            content: "";
            height: 1rem;
            width: 1rem;
            background-color: var(--color-secondary);
            position: absolute;
            bottom: -1.5rem;
            left: 50%;
            transform: translateX(-50%);
          }
        `}
      </style>
    </nav>
  );
}
//Nav-Link
function Link(props) {
  return (
    <a href="/Stories" className="navbar-link">
      {/* {props.children} */}
      <style jsx>
        {`
          
          .nav-link {
            color: grey;
            font-family: Roboto;
            font-size: 16px;
            font-weight: 700;
            line-height: 19px;
            text-align: center;
          }
          .is-active {
            font-size: 1.8rem;
            color: black;
          }
          .is-active::before {
            content: "";
            height: 1rem;
            width: 1rem;
            background-color: var(--color-secondary);
            position: absolute;
            bottom: -1.5rem;
            left: 50%;
            transform: translateX(-50%);
          }
        `}
      </style>
       
    </a>
   
  );

}

export default Navbar;
