function Header() {
    return (
        <header className="header">
            <div className="container">
                <h1 className="title">Mavel's Fearless</h1>
            </div>
            <style jsx>
                {`
                .header {
  width: 100%;
  height: 100vh;
  padding-top: 15rem;
  padding-bottom: 5rem;
  color: var(--color-secondary);
  background-color: var(--color-primary);
  position: relative;
}

.title,
.subtitle {
  width: 60rem;
  max-width: 100%;
}

.title {
  font-size: 7.2rem;
}

.subtitle {
  margin-bottom: 3rem;
}
.container {
  width: 124rem;
  max-width: 100%;
  margin: 0rem auto;
}
@media (max-width: 1280px) {
  .container {
    padding: 0rem 10rem;
  }
}

@media (max-width: 768px) {
  .container {
    padding: 0rem 4rem;
  }
}
`}
           </style>
        </header>
    )
}
export default Header;
